pragma solidity ^0.5.0;

contract MedicalPrescription {
    uint public prescriptionCount = 0;

    // Define Prescription structure
    struct Prescription {
        uint id;
        address doctor;       // Doctor's Ethereum address
        uint16 patientId;     // Patient's ID (uint16)
        address pharmacist;   // Pharmacist's Ethereum address
        string medicineDetails;
        bool fulfilled;
    }

    // Mapping to store prescriptions
    mapping(uint => Prescription) public prescriptions;

    // Events to log contract activities
    event PrescriptionCreated(
        uint id,
        address doctor,
        uint16 patientId,
        address pharmacist,
        string medicineDetails,
        bool fulfilled
    );

    event PrescriptionFulfilled(
        uint id,
        address pharmacist
    );

    // Create a new prescription (only by a doctor)
    function createPrescription(uint16 _patientId, address _pharmacist, string memory _medicineDetails) public {
        prescriptionCount++;
        prescriptions[prescriptionCount] = Prescription(
            prescriptionCount,
            msg.sender, // Doctor's address
            _patientId,
            _pharmacist,
            _medicineDetails,
            false // Initially not fulfilled
        );
        emit PrescriptionCreated(prescriptionCount, msg.sender, _patientId, _pharmacist, _medicineDetails, false);
    }

    // Fulfill a prescription (only by the assigned pharmacist)
    function fulfillPrescription(uint _id) public {
        Prescription storage prescription = prescriptions[_id];

        require(msg.sender == prescription.pharmacist, "Only the assigned pharmacist can fulfill this prescription.");
        require(!prescription.fulfilled, "This prescription is already fulfilled.");

        prescription.fulfilled = true;

        emit PrescriptionFulfilled(_id, msg.sender);
    }

    // View a prescription (public visibility)
    function viewPrescription(uint _id) public view returns (
        uint, address, uint16, address, string memory, bool
    ) {
        Prescription memory prescription = prescriptions[_id];
        return (
            prescription.id,
            prescription.doctor,
            prescription.patientId,
            prescription.pharmacist,
            prescription.medicineDetails,
            prescription.fulfilled
        );
    }
}
