pragma solidity ^0.5.0;

contract MedicalPrescription {
    uint public prescriptionCount = 0;

    // Define Prescription structure
    struct Prescription {
        uint id;
        address doctor;       // Doctor's Ethereum address
        uint16 patientId;     // Patient's ID
        string drug;          // Name of the prescribed drug
        string dosage;        // Dosage details
        string direction;     // Directions for use
        string comments;      // Additional comments or notes
        bool fulfilled;       // Fulfillment status
    }

    // Mapping to store prescriptions
    mapping(uint => Prescription) public prescriptions;

    // Events to log contract activities
    event PrescriptionCreated(
        uint id,
        address doctor,
        uint16 patientId,
        string drug,
        string dosage,
        string direction,
        string comments,
        bool fulfilled
    );

    event PrescriptionFulfilled(
        uint id,
        address pharmacist
    );

    // Create a new prescription (only by a doctor)
    function createPrescription(
        uint16 _patientId, 
        string memory _drug, 
        string memory _dosage, 
        string memory _direction, 
        string memory _comments
    ) 
        public 
    {
        prescriptionCount++;
        prescriptions[prescriptionCount] = Prescription(
            prescriptionCount,
            msg.sender, // Doctor's address
            _patientId,
            _drug,
            _dosage,
            _direction,
            _comments,
            false // Initially not fulfilled
        );
        emit PrescriptionCreated(
            prescriptionCount, 
            msg.sender, 
            _patientId, 
            _drug, 
            _dosage, 
            _direction, 
            _comments, 
            false
        );
    }

    // Fulfill a prescription (any pharmacist)
    function fulfillPrescription(uint _id) public {
        Prescription storage prescription = prescriptions[_id];

        require(!prescription.fulfilled, "This prescription is already fulfilled.");

        prescription.fulfilled = true;

        emit PrescriptionFulfilled(_id, msg.sender);
    }

    // View a prescription (public visibility)
    function viewPrescription(uint _id) public view returns (
        uint, address, uint16, string memory, string memory, string memory, string memory, bool
    ) {
        Prescription memory prescription = prescriptions[_id];
        return (
            prescription.id,
            prescription.doctor,
            prescription.patientId,
            prescription.drug,
            prescription.dosage,
            prescription.direction,
            prescription.comments,
            prescription.fulfilled
        );
    }
}
